import React, {Component} from 'react';

class BuildingList extends Component {
	render() {
		const { data, selectedUpdate, filterText } = this.props;
		const buildingList = data
			.filter(directory => {
				return (
					directory.name.toLowerCase().indexOf(filterText.toLowerCase()) >= 0
				)
			})
			.map(directory => {
				return (
					<tr key = {directory.id}>
						<button style="margin:5px;" onClick = { () => selectedUpdate(directory.id) }>
							{directory.code} 
							{' '}
							{directory.name} 
						</button>
					</tr>
				)
			});
		return (
			<div>
				<p>
					<i>Click on a building to view listing information</i>
				</p>
				{buildingList}
			</div>
		)
	}
}
export default BuildingList;
