import React, {Component} from 'react';

class ViewBuilding extends Component {
	render() {
		const { data, selectedBuilding } = this.props;
		const buildingData = data
			.filter(building => {
				return building.id === selectedBuilding
			})
			.map(building => {
				let coord, addr = '';
				if(typeof building.coordinates == 'undefined'
					&& !(isNaN(building.coordinates.latitude) || isNaN(building.coordinates.longitude))) {
						coord = building.coordinates.latitude + ', ' + building.coordinates.longitude;
				} else {
					coord = 'Not available';
				}
				if (typeof building.address == 'undefined') 
					addr = 'Not available ';
				else
					addr = building.address;
				return (
					<div>
						<tr>
							<row>
								<b> 
									Code: 
								</b>
								{building.code} 	
							</row>
						</tr>
						<tr>
							<row>
								<b> 
									Name: 
								</b>
								{building.name} 
							</row>
						</tr>
						<tr>
							<row>
								<b> 
									Coordinates: 
								</b>
								{coord} 
							</row>
						</tr>
						<tr>
							<row>
								<b> 
									Address:  
								</b>
								{addr} 
							</row>
						</tr>
					</div>
				);
			});

		return (
			<div>
				<p>
					<i>Click on a building to view listing information</i>
					<ul>
						{buildingData}
					</ul>
				</p>
			</div>
		);
	}
}

export default ViewBuilding;
